    private async Task HandleComment()
    {
        if (await localStorage.GetItemAsync<UserDTO>
                (SD.Local_UserDetails) != null)
        {
            var userInfo = await localStorage.GetItemAsync<UserDTO>
                (SD.Local_UserDetails);
